package com.noorapp.noor.adapter;import android.content.Context;import android.content.Intent;import android.graphics.Color;import android.graphics.Paint;import android.support.annotation.NonNull;import android.support.v7.widget.CardView;import android.support.v7.widget.RecyclerView;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.LinearLayout;import android.widget.TextView;import com.noorapp.noor.Constants;import com.noorapp.noor.Details_Activity;import com.noorapp.noor.R;import com.noorapp.noor.models.Place;import com.noorapp.noor.models.PlaceFilterModel;import com.squareup.picasso.Picasso;import java.util.ArrayList;import java.util.List;import static com.noorapp.noor.Discover_frag.filterList;public class PlacesFilterAdaptercard extends RecyclerView.Adapter<PlacesFilterAdaptercard.FilmsViewHolder> {    private Context mCtx;    private List<PlaceFilterModel> placeList;    private List<Place> placesList;    public static List<String> filtercheck;    public PlacesFilterAdaptercard(Context mCtx, List<PlaceFilterModel> filmList,List<Place>placesList) {        this.mCtx = mCtx;        this.placeList = filmList;        this.placesList=placesList;        filtercheck = new ArrayList<>();    }    @NonNull    @Override    public FilmsViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view = LayoutInflater.from(parent.getContext()).inflate(R.layout.filter_card, parent, false);        return new FilmsViewHolder(view);    }    @Override    public int getItemViewType(int position) {        return position;    }    @Override    public void onBindViewHolder(@NonNull final FilmsViewHolder holder, int position) {        final PlaceFilterModel results = placeList.get(position);        if (!results.getName().isEmpty()) {            holder.title.setText(results.getName());            holder.cardView.setOnClickListener(new View.OnClickListener() {                @Override                public void onClick(View v) {                    if(results.isExist()==false){                        holder.cardView.setCardBackgroundColor(Color.parseColor("#e9ab3d"));                        results.setExist(true);                        filtercheck.add(results.getName());                        filterList(filtercheck, placesList);                    }else{                        holder.cardView.setCardBackgroundColor(Color.parseColor("#FFFFFF"));                        results.setExist(false);                        filtercheck.remove(results.getName());                        filterList(filtercheck, placesList);                    }                }            });        }    }    @Override    public int getItemCount() {        return placeList.size();    }    public class FilmsViewHolder extends RecyclerView.ViewHolder {        TextView title;        CardView cardView;        public FilmsViewHolder(View itemview) {            super(itemview);            title = itemView.findViewById(R.id.stadtxt);            cardView = itemView.findViewById(R.id.stad);        }    }}